script:
frappe.ui.form.on("Item", "refresh", async function(frm) {

	// item.js loads Stock Levels only for non-fixed assets
	if (frm.doc.is_fixed_asset)
		return;

	// sleep 1.5sec..  
	await sleep(1500);

	frappe.after_ajax(function() {
		// custom method
		frappe.call({
			"method" : "ctechnology_erpnext.api.item_warehouse_valuationrate",

			// takes two arguments., item and warehouse=None
			args : {
				item : frm.doc.name
			},

			callback : function(data){
				if (!data.message)
					return;

				var _currency = frappe.defaults.get_global_default('currency');
				// simplicity?
				var get_cost = function(warehouse){

					// var to hold empty/cost
					var cost;
					$.each(data.message, function(i, obj)
					{						
							if (obj.warehouse == warehouse){
								cost = obj.valuation_rate;
								// $.each breaks if returned false
								return false;
							}
					});
					// format currency read from sbkolate.com
					return format_currency(cost, currency= _currency);
				};

				// in .form-dashboard-section.custom, child .result --- iterating each children of .result
				$('.form-dashboard-section.custom .result').children().each(function(i, obj)
				{
					// etching warehouse val and modifying items col and graph col
					// warehouse -- 1st col, items col -- 2nd col, graph - 3rd
					// nth-child -- 1-indexed
					var warehouse = $(this).find('.row > div:nth-child(1) > a').attr('data-name');
					$(this).find('.row > div:nth-child(2)').addClass('col-sm-2').removeClass('col-sm-3');
					$(this).find('.row > div:nth-child(3)').addClass('col-sm-3').removeClass('col-sm-4');

					$(this).find('.row > div:nth-child(2)').after('<div class="col-sm-2 small" style="margin-top: 8px; white-space: nowrap;"><h6 class="uppercase" style="display: inline;">Cost</h6>&ensp;'+ get_cost(warehouse) +'</div>');
				});
			}
		});
	});
});

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

/*
		Has Qty (Show items which has balance qty only)
		Territory Mode of Payment
		Cost per Warehouse in Sales Invoice Item
		Custom Warehouse field (Header)
		Custom Av. Qty (Sales Invoice Item)
*/


frappe.ui.form.on("Sales Invoice", {
	refresh : function(frm, cdt, cdn) {
		// console.dir(cur_frm.fields_dict.items.grid.get_field("item_code"));

		frm.set_query("item_code", "items", function(doc, cdt, cdn) {
			var d = locals[cdt][cdn];
			console.dir(d);
			if (frm.doc.consoleerp_hasqty == 1) {

				if (!d.warehouse){
					show_alert("Warehouse not selected in the Grid");
					frm.doc.consoleerp_hasqty = 0;
					frm.refresh_field("consoleerp_hasqty");
					return;
				}

				return { 
					query : "ctechnology_erpnext.controllers.queries.hasqty_item_query",
					filters : {"warehouse" : d.warehouse}
				}
			} else {
				return { }
			}
		});			
	},

	// Mode of payment per territory
	territory : function(frm, cdt, cdn) {

		// ref
		var xdoc = frm.doc;

		// if territory is null, return
		if (!xdoc.territory || !xdoc.is_pos)
			return;

		frappe.after_ajax(function() {
			frappe.call({
				'method' : 'frappe.client.get_value',
				args : {
					doctype: "Mode of Payment",
					filters : {
						"consoleerp_territory" : frm.doc.territory
					},
					fieldname : ["name", "type"],
					as_dict: true
				},
				callback: function(data) {
					if (!data.message)
						return;

					// clear payments table in Sales Invoice
					xdoc.payments = [];
					// add new child; payment-- is the doc obj
					var payment = frappe.model.add_child(xdoc, "Sales Invoice Payment", "payments");
					payment.mode_of_payment = data.message.name;
					payment.amount = xdoc.rounded_total;
					// set as Cash type by default
					payment.type = data.message.type;

					// defined in /erpnext/public/js/controllers/accounts.js
					get_payment_mode_account(frm, data.message.name, function(account){
						payment.account = account;		
						refresh_field("payments");
					});
				}
			});
		});
	},

	// Header Warehouse
	consoleerp_warehouse : function(frm, cdt, cdn) {
		$.each(frm.doc.items, function(i, child_doc){			
			frappe.model.set_value("Sales Invoice Item", child_doc.name, "warehouse", frm.doc.consoleerp_warehouse);
		});
	}

});



frappe.ui.form.on('Sales Invoice Item', {
	// cost + header warehouse
	item_code : function(frm, cdt, cdn) {
		cust_fetch_item_cost_with_warehouse(frm, cdt,cdn);		
		updateHeaderWarehouse(frm, cdt, cdn);
	},
	warehouse : function(frm, cdt, cdn) {
		cust_fetch_item_cost_with_warehouse(frm, cdt,cdn);		
	}
});


var updateHeaderWarehouse = function(frm, cdt, cdn) {
	frappe.after_ajax(function() {
		frappe.model.set_value(cdt, cdn, "warehouse", frm.doc.consoleerp_warehouse);
	});
}
var updateCustomAvQty = function(frm, cdt, cdn) {

	var d = locals[cdt][cdn];
	frappe.after_ajax(function() {		
		show_alert(cdt + ":" + cdn);
		frappe.model.set_value(cdt, cdn, "consoleerp_actual_qty", d.actual_qty);
	});
}


var cust_fetch_item_cost_with_warehouse = async function(frm, cdt, cdn) {
	// null val
	frappe.model.set_value(cdt, cdn, "consoleerp_cost", null);
	// return if not updating stock
	// cur_frm -- global variable for current_form
	if (!cur_frm.doc.update_stock) {
		show_alert("Not Updating Stock", 1);
		return;
	}

	item = frappe.model.get_value(cdt, cdn, "item_code");
	warehouse = frappe.model.get_value(cdt, cdn, "warehouse");
	if (!item || !warehouse) {
		show_alert("Item or Warehouse Missing", 3);
		return;	
	}

	frappe.after_ajax(function(){
		frappe.call({
				"method": "ctechnology_erpnext.api.item_warehouse_valuationrate",
				args: {
					item: item,
					warehouse: warehouse
				},
				callback: function(data) {
					if (data.message == null || (data.message.constructor === Array && data.message.length == 0))
					{
						data.message = [{"valuation_rate" : null, "actual_qty" : 0}];						
					}
					console.dir(data.message);
					frappe.model.set_value(cdt, cdn, "consoleerp_cost", data.message[0].valuation_rate);	
					frappe.model.set_value(cdt, cdn, "consoleerp_actual_qty", data.message[0].actual_qty);	
				}
			});
		});
}
